    //         {/* <Introduction /> */}
    //         {/* <Work /> */}
    //         {/* <Skills /> */}
    //         {/* <Projects /> */}
    //         {/* <Contact />   */}

    //         <Footer />            
             
    //     </main>
    // </LocomotiveScrollProvider>
  );
}
export default App;
